-- --------------------------------------------------------------------------------------
-- Trigger para expiración de contraseñas y expiración automática de contraseñas antiguas 
-- --------------------------------------------------------------------------------------
DELIMITER //
CREATE TRIGGER before_insert_contrasena 
BEFORE INSERT ON contrasena 
FOR EACH ROW 
BEGIN
    IF EXISTS (
        SELECT 1 
        FROM contrasena 
        WHERE idUsuario = NEW.idUsuario 
        AND clave = NEW.clave 
        ORDER BY fechaCreacion DESC 
        LIMIT 3
    ) THEN
        SIGNAL SQLSTATE '45000' 
        SET MESSAGE_TEXT = 'La contraseña ha sido utilizada recientemente. Elige otra contraseña.';
    END IF;

END//
DELIMITER ;


