DELIMITER //
CREATE TRIGGER before_insert_puedevisualizar
BEFORE INSERT ON puedevisualizar
FOR EACH ROW
BEGIN
   IF NOT EXISTS (
       SELECT 1 FROM documento
       WHERE idDocumento = NEW.idDocumento
       AND visibilidad = 'Publico'
   ) THEN
